template<class K, class V>
struct map_list_of_type {
	typedef std::map<K, V> Map;
	Map data;
	map_list_of_type(K k, V v) { data[k] = v; }
	map_list_of_type& operator()(K k, V v) { data[k] = v; return *this; }
	operator Map const&() const { return data; }
}; 

